import Footer from "@/Components/Footer.vue";
import useValidate from '@vuelidate/core'
import { required, email, minLength, sameAs, numeric } from '@vuelidate/validators'

export default {
  components: {
    Head,
    Link,
    UpperShopNav,
    Banner,
    LowerShopNav,
    Menu,
    Footer,
  },
  
  data(){
    return {
      v$: useValidate(),
      form: {
        firstName : "", 
        lastName : "",
        email : "",
        telephone: "",
        fax: "",
        company: "",
        address_1: "",
        address_2: "",
        city: "",
        postcode: "",
        country_id: "",
        zone_id: "",
        password: "",
        confirm: ""
      },
    }
  },

  validations(){
    return {
      form: {
        firstName : {required}, 
        lastName : {required},
        email : {required, email},
        telephone: {required, numeric},
        fax: {},
        company:{},
        address_1: {required},
        address_2: {},
        city: {required},
        postcode:{},
        country_id:{required},
        zone_id: {required},
        password: {required, minLength: minLength(6)},
        confirm: {required, sameAs: sameAs(this.form.password) }
      },
    }
  },
  methods:{

    submit(){
      
      this.v$.$validate()
      console.log('here')
      
      if(!this.v$.$error){
        console.log('Form succeeded validation')
        this.$inertia.post('account',this.form)
      }
      else{
        
        console.log('Form failed validation')
      }
    }
  },

  
  
};
</script>


